import type { ExtensionAPI } from "@mariozechner/pi-coding-agent";
import { truncateToWidth, visibleWidth, matchesKey, Key } from "@mariozechner/pi-tui";

class BoxEditor {
  private text = "";
  private cursorPos = 0;
  private theme: any;
  private keybindings: any;
  private tui: any;
  public focused = false;

  constructor(theme: any, keybindings: any, tui: any) {
    this.theme = theme;
    this.keybindings = keybindings;
    this.tui = tui;
  }

  getText(): string {
    return this.text;
  }

  setText(text: string): void {
    this.text = text;
    this.cursorPos = text.length;
  }

  clear(): void {
    this.text = "";
    this.cursorPos = 0;
  }

  insert(text: string): void {
    const before = this.text.slice(0, this.cursorPos);
    const after = this.text.slice(this.cursorPos);
    this.text = before + text + after;
    this.cursorPos += text.length;
  }

  backspace(): void {
    if (this.cursorPos > 0) {
      const before = this.text.slice(0, this.cursorPos - 1);
      const after = this.text.slice(this.cursorPos);
      this.text = before + after;
      this.cursorPos--;
    }
  }

  delete(): void {
    if (this.cursorPos < this.text.length) {
      const before = this.text.slice(0, this.cursorPos);
      const after = this.text.slice(this.cursorPos + 1);
      this.text = before + after;
    }
  }

  moveLeft(): void {
    if (this.cursorPos > 0) this.cursorPos--;
  }

  moveRight(): void {
    if (this.cursorPos < this.text.length) this.cursorPos++;
  }

  moveHome(): void {
    this.cursorPos = 0;
  }

  moveEnd(): void {
    this.cursorPos = this.text.length;
  }

  handleInput(data: string): void {
    // Handle keybindings first
    if (this.keybindings) {
      const handled = this.keybindings.handle(data, this.tui);
      if (handled) return;
    }

    // Handle special keys
    if (matchesKey(data, Key.left)) {
      this.moveLeft();
    } else if (matchesKey(data, Key.right)) {
      this.moveRight();
    } else if (matchesKey(data, Key.home)) {
      this.moveHome();
    } else if (matchesKey(data, Key.end)) {
      this.moveEnd();
    } else if (matchesKey(data, Key.backspace)) {
      this.backspace();
    } else if (matchesKey(data, Key.delete)) {
      this.delete();
    } else if (data.length === 1 && data.charCodeAt(0) >= 32) {
      // Printable character
      this.insert(data);
    }

    this.tui.requestRender();
  }

  render(width: number): string[] {
    // Calculate dimensions
    const contentWidth = width - 4; // Account for  and spaces:  > text 

    // Truncate content if needed for display
    const displayContent = truncateToWidth(this.text, contentWidth - 2, "");

    // Build the box border
    const horizontal = "".repeat(width - 2);
    const topBorder = `${horizontal}`;
    const bottomBorder = `${horizontal}`;

    // Content line with orange > prompt
    const prompt = this.theme.fg("accent", ">");
    const padding = " ".repeat(Math.max(0, contentWidth - 2 - visibleWidth(displayContent)));
    const contentLine = ` ${prompt} ${displayContent}${padding} `;

    return [topBorder, contentLine, bottomBorder];
  }

  invalidate(): void {
    // Nothing to cache
  }
}

export default function (pi: ExtensionAPI) {
  pi.on("session_start", (_event, ctx) => {
    ctx.ui.setEditorComponent((tui, theme, keybindings) =>
      new BoxEditor(theme, keybindings, tui) as any
    );
  });
}

