import { CustomEditor, type ExtensionAPI } from "@mariozechner/pi-coding-agent";
import { truncateToWidth, visibleWidth } from "@mariozechner/pi-tui";

class BoxEditor extends CustomEditor {
	render(width: number): string[] {
		// Get content from parent render (which handles cursor, scrolling, etc.)
		const innerWidth = width - 4; // Space for  and padding
		const lines = super.render(innerWidth);

		if (lines.length === 0) return lines;

		// Build the box borders
		const horizontal = "".repeat(width - 2);
		const topBorder = `${horizontal}`;
		const bottomBorder = `${horizontal}`;

		// Style the content lines with orange > prompt on first line
		const styledLines = lines.map((line, index) => {
			const cleanLine = line.replace(/^>\s*/, "");
