import { CustomEditor, type ExtensionAPI } from "@mariozechner/pi-coding-agent";
import { truncateToWidth, visibleWidth } from "@mariozechner/pi-tui";

// Full theme type that includes fg() method
interface FullTheme {
	fg: (color: string, text: string) => string;
	bg: (color: string, text: string) => string;
}

class BoxEditor extends CustomEditor {
	private fullTheme: FullTheme;

	constructor(tui: any, theme: any, keybindings: any, options?: any) {
		super(tui, theme, keybindings, options);
		this.fullTheme = theme as FullTheme;
	}

	render(width: number): string[] {
		// Get content from parent render (which handles cursor, scrolling, etc.)
		const innerWidth = width - 4; // Space for  and padding
		const lines = super.render(innerWidth);

		if (lines.length === 0) return lines;

		// Build the box borders
		const horizontal = "".repeat(width - 2);
		const topBorder = `${horizontal}`;
		const bottomBorder = `${horizontal}`;

		// Style the content lines with orange > prompt on first line
		const styledLines = lines.map((line, index) => {
			const cleanLine = line.replace(/^>\s*/, "");
			const paddedLine = truncateToWidth(cleanLine, innerWidth - 2, "");
			const padding = " ".repeat(Math.max(0, innerWidth - 2 - visibleWidth(paddedLine)));

			if (index === 0) {
				// First line gets the orange > prompt
				const prompt = this.fullTheme.fg("accent", ">");
				return ` ${prompt} ${paddedLine}${padding} `;
			}
			// Additional lines (wrapped text)
			return `   ${paddedLine}${padding} `;
		});

		return [topBorder, ...styledLines, bottomBorder];
	}
}

export default function (pi: ExtensionAPI) {
	pi.on("session_start", (_event, ctx) => {
		ctx.ui.setEditorComponent((tui, theme, kb) => new BoxEditor(tui, theme, kb));
	});
}

import { CustomEditor, type ExtensionAPI } from "@mariozechner/pi-coding-agent";
import { truncateToWidth, visibleWidth, CURSOR_MARKER } from "@mariozechner/pi-tui";

// Full theme type that includes fg() method
interface FullTheme {
	fg: (color: string, text: string) => string;
	bg: (color: string, text: string) => string;
}

class BoxEditor extends CustomEditor {
	private fullTheme: FullTheme;

	constructor(tui: any, theme: any, keybindings: any, options?: any) {
		super(tui, theme, keybindings, options);
		this.fullTheme = theme as FullTheme;
	}

	render(width: number): string[] {
		// Get the raw text content
		const text = this.getText();
		const lines = text.split("\n");

		// Calculate dimensions
		const contentWidth = width - 4; //  > content  = 4 chars

		// Build the box borders
		const horizontal = "".repeat(width - 2);
		const topBorder = `${horizontal}`;
		const bottomBorder = `${horizontal}`;

		// Format each content line
		const contentLines = lines.map((line, index) => {
			const paddedLine = truncateToWidth(line, contentWidth - 2, "");
			const padding = " ".repeat(Math.max(0, contentWidth - 2 - visibleWidth(paddedLine)));

			if (index === 0) {
				// First line gets the orange > prompt
				const prompt = this.fullTheme.fg("accent", ">");
				return ` ${prompt} ${paddedLine}${padding} `;
			}
			// Additional lines (indented)
			return `   ${paddedLine}${padding} `;
		});

		// If empty, show just the prompt
		if (contentLines.length === 0 || (contentLines.length === 1 && text === "")) {
			const prompt = this.fullTheme.fg("accent", ">");
			const emptyPadding = " ".repeat(contentWidth - 2);
			contentLines[0] = ` ${prompt} ${emptyPadding} `;
		}

		return [topBorder, ...contentLines, bottomBorder];
	}
}

export default function (pi: ExtensionAPI) {
	pi.on("session_start", (_event, ctx) => {
		ctx.ui.setEditorComponent((tui, theme, kb) => new BoxEditor(tui, theme, kb));
	});
}

