import { CustomEditor, type ExtensionAPI } from "@mariozechner/pi-coding-agent";
import { truncateToWidth, visibleWidth } from "@mariozechner/pi-tui";

// Store the full theme reference
let fullTheme: any = null;

// Strip default border characters from parent render output
function cleanParentLine(line: string): string {
	// Remove leading/trailing border characters and spaces
	// Parent renders like: "> text" or "  text" or ""
	// We want to keep the content but style it our way

	// If it's a border line (all  or empty), return empty
	if (/^[\s]*$/.test(line.replace(/\x1b\[[0-9;]*m/g, ""))) {
		return "";
	}

	// Remove leading "> " or "  " prompt/spacing from parent
	return line.replace(/^(>\s+|\s{2})/, "");
}

class BoxEditor extends CustomEditor {
	render(width: number): string[] {
		// Call parent render to get editor content with autocomplete
		const innerWidth = width - 4; // Account for  and spaces
		const parentLines = super.render(innerWidth);

		if (parentLines.length === 0) return parentLines;

		// Build the box borders
		const horizontal = "".repeat(width - 2);
		const topBorder = `${horizontal}`;
		const bottomBorder = `${horizontal}`;

		// Process parent lines - filter out border-only lines and clean content
		const contentLines: string[] = [];
		let isFirstContent = true;

		for (const line of parentLines) {
			const cleanLine = cleanParentLine(line);

			// Skip empty/border lines
			if (!cleanLine && !line.includes("[7m")) continue;

			// Calculate padding
			const visibleLen = visibleWidth(line);
			const padding = " ".repeat(Math.max(0, innerWidth - visibleLen));

			if (isFirstContent && fullTheme) {
				// First content line gets styled orange >
				const prompt = fullTheme.fg("accent", ">");
				contentLines.push(` ${prompt} ${cleanLine}${padding} `);
				isFirstContent = false;
			} else {
				// Other lines (autocomplete, cursor, etc.)
				contentLines.push(`   ${line}${padding} `);
			}
		}

		// If no content lines, show empty prompt
		if (contentLines.length === 0) {
			if (fullTheme) {
				const prompt = fullTheme.fg("accent", ">");
				const emptyPadding = " ".repeat(innerWidth - 2);
				contentLines.push(` ${prompt} ${emptyPadding} `);
			} else {
				const emptyPadding = " ".repeat(innerWidth);
				contentLines.push(` > ${emptyPadding} `);
			}
		}

		return [topBorder, ...contentLines, bottomBorder];
	}
}

export default function (pi: ExtensionAPI) {
	pi.on("session_start", (_event, ctx) => {
		// Store reference to full theme
		fullTheme = ctx.ui.theme;

		ctx.ui.setEditorComponent((tui, theme, kb) => new BoxEditor(tui, theme, kb));
	});
}

