            break;
        }
    }
    if (isPureAscii) {
        return str.length;
    }
    // Check cache
    const cached = widthCache.get(str);
    if (cached !== undefined) {
        return cached;
    }
    // Normalize: tabs to 3 spaces, strip ANSI escape codes
    let clean = str;
    if (str.includes("\t")) {
        clean = clean.replace(/\t/g, "   ");
    }
    if (clean.includes("\x1b")) {
